import { jsPDF } from "jspdf";
import autoTable, { Styles } from "jspdf-autotable";
import { Injectable } from "@nestjs/common";
import { AppUtils } from "../lib";

@Injectable()
export class PdfService {
  private tableId: string;
  private headerStyle: Partial<Styles>;
  // https://www.base64-image.de
  private footer = "data:image/png;base64,";
  // private footer = "https://rk-staging.web.app/assets/img/footer.png"; // throws error "malformed png" so I used base64 instead
  private footerType = "PNG";
  private title = "";

  constructor() {
    // https://github.com/simonbengtsson/jsPDF-AutoTable
    // npm install jspdf jspdf-autotable
    // Color: Either false for transparent, hex string,
    // gray level 0-255 or rbg array e.g. [255, 0, 0] false|string|number|[number, number, number]
    // Padding: Either a number or object {top: number, right: number, bottom: number, left: number}
    this.headerStyle = {
      font: "times", // 'helvetica'|'times'|'courier'
      fontStyle: "normal", // 'normal'|'bold'|'italic'|'bolditalic'
      overflow: "linebreak", // 'linebreak'|'ellipsize'|'visible'|'hidden'
      fillColor: "#007ac3", // false|string|number|[number, number, number]
      textColor: "#ffffff", // Color ? number
      lineColor: "#007ac3" // Color
      // cellWidth: 'auto', // 'auto' | 'wrap' | number = 'auto'
      // minCellWidth: 10, // number ?
      // minCellHeight: 0, // number
      // halign: 'center', // 'left' | 'center' | 'right'
      // valign: 'middle', // 'top' | 'middle' | 'bottom'
      // fontSize: 14, // number 0.875rem
      // cellPadding: 0, // Padding
      // lineWidth: 0 // number,
    };
  }

  setTableId(id: string) {
    this.tableId = id;
    return this;
  }

  setHeaderStyle(styles: any) {
    this.headerStyle = styles;
    return this;
  }

  /*head - [['ID', 'Name', 'Country']], body - [['1', 'Simon', 'Sweden'], ['2', 'Karl', 'Norway']]*/
  exportPdf(fileName = "report",
            head: string[][],
            body: string[][]) {
    return new Promise<ArrayBuffer>((resolve, reject) => {
      if (!AppUtils.stringIsSet(fileName)) {
        return reject("set file name and try again !");
      }
      if (head.length === 0) {
        return reject(`set table headers for ${fileName} and try again !`);
      }
      if (body.length === 0) {
        return reject(`set table rows for ${fileName} and try again !`);
      }
      try {
        const doc = new jsPDF();
        autoTable(doc, {
          head, //  [['ID', 'Name', 'Country']]
          body, // [['1', 'Simon', 'Sweden'], ['2', 'Karl', 'Norway']]
          theme: "grid",
          margin: { bottom: 30 },
          headStyles: this.headerStyle,
          includeHiddenHtml: false, // for hidden footer and header elements
          didDrawPage: (data) => {
            const pageSize = doc.internal.pageSize;
            const totalPages = doc.getNumberOfPages();
            const pageHeight = pageSize.height ? pageSize.height : pageSize.getHeight();
            const pageWidth = pageSize.width ? pageSize.width : pageSize.getWidth();
            const marginLeft = data.settings.margin.left;
            const marginRight = data.settings.margin.right;
            const marginTop = data.settings.margin.top;
            // const marginBottom = data.settings.margin.bottom;
            // const fileWidth = pageWidth - marginLeft - marginLRight;
            doc.setFontSize(10);
            doc.setTextColor("#000000");
            if (!AppUtils.stringIsSet(this.title)) {
              this.title = fileName;
            }
            const titleX = (marginLeft + (pageWidth / 4)) - marginRight;
            const titleY = marginTop - 2;
            doc.text(this.title.toUpperCase(), titleX, titleY);
            const fileWidth = pageWidth;
            const fileHeight = 20;
            const x = 0;
            const y = pageHeight - fileHeight;
            /*const x = marginLeft;
            const y = pageHeight - marginTop - marginBottom;*/
            doc.setFontSize(8);
            let pageNumber = "Page " + doc.getCurrentPageInfo().pageNumber;
            // Total page number plugin only available in jspdf v1.0+
            if (typeof doc.putTotalPages === "function") {
              // pageNumber = pageNumber + ' of ' + totalPages;
            }
            const pageX = (marginLeft + (pageWidth / 2)) - marginRight;
            const pageY = y - 2;
            doc.text(pageNumber, pageX, pageY);
            // doc.addImage(this.footer, this.footerType, x, y, fileWidth, fileHeight);
          }
        });
        /*autoTable(doc, {
          head: [this.header],
          body: [data],
          theme: 'grid',
          didDrawCell: (cellData: any) => {
            console.log(cellData);
          }
        });*/
        // Open PDF document in new tab
        // doc.output('dataurlnewwindow');
        return resolve(doc.output("arraybuffer"));
      } catch (e) {
        return reject(e);
      }
    });
  }

  exportPdfMultiple(fileName = "report", tables: {
    head: string[][],
    body: string[][]
  }[]) {
    return new Promise<ArrayBuffer>((resolve, reject) => {
      if (!AppUtils.stringIsSet(fileName)) {
        return reject("set report file name and try again !");
      }
      if (tables.length === 0) {
        return reject("set table(s) and try again !");
      }
      try {
        const headStyles: any = {
          font: "times", // 'helvetica'|'times'|'courier'
          fontStyle: "normal", // 'normal'|'bold'|'italic'|'bolditalic'
          overflow: "linebreak", // 'linebreak'|'ellipsize'|'visible'|'hidden'
          fillColor: "#ffffff", // false|string|number|[number, number, number]
          textColor: "#000000", // Color ? number
          lineColor: "#000000" // Color
          // cellWidth: 'auto', // 'auto' | 'wrap' | number = 'auto'
          // minCellWidth: 10, // number ?
          // minCellHeight: 0, // number
          // halign: 'center', // 'left' | 'center' | 'right'
          // valign: 'middle', // 'top' | 'middle' | 'bottom'
          // fontSize: 14, // number 0.875rem
          // cellPadding: 0, // Padding
          // lineWidth: 0 // number,
        };
        const doc = new jsPDF();
        for (const table of tables) {
          const head = table.head;
          const body = table.body;
          autoTable(doc, {
            head, //  [['ID', 'Name', 'Country']]
            body, // [['1', 'Simon', 'Sweden'], ['2', 'Karl', 'Norway']]
            theme: "grid",
            margin: { bottom: 30 },
            headStyles,
            includeHiddenHtml: false, // for hidden footer and header elements
            didDrawPage: (data) => {
              const pageSize = doc.internal.pageSize;
              const totalPages = doc.internal.pages.length;
              const pageHeight = pageSize.height ? pageSize.height : pageSize.getHeight();
              const pageWidth = pageSize.width ? pageSize.width : pageSize.getWidth();
              const marginLeft = data.settings.margin.left;
              const marginRight = data.settings.margin.right;
              const marginTop = data.settings.margin.top;
              // const marginBottom = data.settings.margin.bottom;
              // const fileWidth = pageWidth - marginLeft - marginLRight;
              doc.setFontSize(10);
              doc.setTextColor("#000000");
              if (!AppUtils.stringIsSet(this.title)) {
                this.title = fileName;
              }
              const titleX = (marginLeft + (pageWidth / 4)) - marginRight;
              const titleY = marginTop - 2;
              doc.text(this.title.toUpperCase(), titleX, titleY);

              doc.setFontSize(8);
              doc.setTextColor("#000000");
              const fileWidth = pageWidth;
              const fileHeight = 20;
              const x = 0;
              const y = pageHeight - fileHeight;
              /*const x = marginLeft;
              const y = pageHeight - marginTop - marginBottom;*/
              const pageNumber = "Page " + doc.getCurrentPageInfo().pageNumber;
              // Total page number plugin only available in jspdf v1.0+
              if (typeof doc.putTotalPages === "function") {
                // pageNumber = pageNumber + ' of ' + totalPages;
              }
              const pageX = (marginLeft + (pageWidth / 2)) - marginRight;
              const pageY = y - 2;
              doc.text(pageNumber, pageX, pageY);
              // doc.addImage(this.footer, this.footerType, x, y, fileWidth, fileHeight);
            }
          });
        }
        return resolve(doc.output("arraybuffer"));
      } catch (e) {
        return reject(e);
      }
    });
  }
}
